/**@jsx jsx */
import { css, jsx } from '@emotion/core';
import { colors } from '../../../lib/styles/global';
import { formatDate } from '../../../lib/Utils/utils';
import Maybe from '../../Maybe/Maybe';
import Tags from './Tags';
import { breakpoints } from '../../../lib/styles/responsive';
import Utterances from '../../Utterances/Utterances';

export type PostProps = {
	title: string;
	preContent: string;
	date: string;
	onClick?: () => void;
	imgUrl?: string;
	tagArr: string[];
};
export default function Post({ title, preContent, date, onClick, imgUrl, tagArr }: PostProps) {
	return (
		<WrpperProps onClick={onClick} imgUrl={imgUrl} dateColor={colors.gray4}>
			<div className="image-container">
				<img alt="" className="thumb-nail" src={imgUrl}/>
			</div>
			<div className="li-article">
				<div className="post-info">
					<h3 className="title">
						{title}
					</h3>
					<Maybe isVisible={tagArr}>
						<Tags tagArr={tagArr} />
					</Maybe>
					<span className="date">{formatDate(date)}</span>
				</div>
				<div className="pre-content">{preContent}</div>
			</div>
			<Utterances />
		</WrpperProps>
	);
}

const WrpperProps = (props) => (
	<li
		onClick={props.onClick}
		css={css`
			border-radius: 4px;
			box-shadow: 0px 4px 8px 8px rgba(0, 0, 0, 0.05);
			cursor: pointer;
			.li-article {
				padding: 1rem 1.5rem;
				height: ${props.imgUrl ? '29rem' : '47rem'};
				padding-top: 2rem;
				.post-info {
					// min-height: ${props.imgUrl ? '12rem' : '13rem'};
					min-height: 12rem;
					border-bottom: 1px solid ${props.dateColor};
					.title {
						max-height: 11.5rem;
						overflow: hidden;
						word-break: break-all;
					}
					.tags {
						border-radius: 1.6rem;
						background-color: #ccc;
						margin: 0.5rem;
						padding: 0.5rem;
						line-height: 1.5;
					}
					.date {
						color: ${props.dateColor};
						font-size: 1.5rem;
						margin-bottom: 2rem;
					}
				}
				.pre-content {
					margin-top: 1.5rem;
					color: #868e96;
					font-size: 1.5rem;
					overflow: hidden;
					max-height: ${props.imgUrl ? '15rem' : '32rem'};
					line-height: 1.9;
					word-break: break-word;
				}
			}
			.image-container {
				overflow: hidden;
				border-radius: 4px 4px 0 0;
				height: 18rem;
				cursor: pointer;
				display: ${!props.imgUrl && 'none'};
				img {
					object-fit: cover;
					height: 100%;
					width: 100%;
					border-radius: 4px 4px 0 0;
					-webkit-transform: scale(1);
					-moz-transform: scale(1);
					-ms-transform: scale(1);
					-o-transform: scale(1);
					transform: scale(1);
					& :hover {
						transform: scale(1.1);
						-webkit-transform: scale(1.1);
						-moz-transform: scale(1.1);
						-ms-transform: scale(1.1);
						-o-transform: scale(1.1);
						transition: 0.3s;
						-webkit-transition: 0.3s;
						-moz-transition: 0.3s;
						-ms-transition: 0.3s;
						-o-transition: 0.3s;
					}
				}
			}
			@media screen and (max-width: ${breakpoints.xlarge}) {
				.image-container {
					height: 15rem;
				}
				.li-article {
					height: 22rem;
				}
			}
			@media screen and (max-width: ${breakpoints.large}) {
				.image-container {
					// height: 15rem;
					display: none;
				}
				.li-article {
					height: 27rem;
				}
			}
			@media screen and (max-width: ${breakpoints.medium}) {
				.li-article {
					height: 20rem;
					.post-info {
  					border-bottom: none;
					}
					.pre-content {
						max-height: 6rem;
					}
				}
			}
			
		`}

	>
		{props.children}
	</li>
);
