import { call, put } from 'redux-saga/effects';

function isPayloadAction(action) {
  return action.payload !== undefined;
}

export default function createAsyncSaga(asyncActionCreator, promiseCreator) {
  return function* saga(action) {
    try {
      const result = isPayloadAction(action) ? yield call(promiseCreator, action.payload) : yield call(promiseCreator);
      yield put(asyncActionCreator.success(result));
    } catch (e) {
      yield put(asyncActionCreator.failure(e));
    }
  };
}
